{
  "task_id": "equities_aapl_snapshot_001",
  "user_prompt": "Give me a two-sentence market snapshot for AAPL today: current price, 1-day % change, and one concise risk from the latest credible headline.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack_send_slack_message",
    "slack_list_slack_channels",
    "python_execution_execute_python",
    "python_execution_process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top1 = head(articles, 1)",
          "headline = top1[0].title",
          "headline_source = top1[0].publisher",
          "published = top1[0].published_utc"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "current_price",
      "1d_change_pct",
      "risk_headline"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "cite headline source"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.05
    },
    "target_length_range": [
      20,
      50
    ]
  }
}
