{
  "task_id": "equities_nvda_daily_summary_001",
  "user_prompt": "Summarize NVDA today in three bullets: price & % move, trading volume context (high/low vs typical), and one headline that might explain the move.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon: polygon_get_aggs",
    "polygon: polygon_get_news",
    "fmp: fmp_get_quote",
    "fmp: fmp_get_income_statement",
    "fmp: fmp_get_company_profile",
    "tavily: tavily_search",
    "tavily: tavily_extract",
    "slack: send_slack_message",
    "slack: list_slack_channels",
    "python_execution: execute_python",
    "python_execution: process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume",
          "volAvg"
        ],
        "compute": [
          "vol_vs_avg_pct = (volume - volAvg) / volAvg * 100"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0",
          "volAvg > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "lead = head(articles, 1)",
          "headline = lead[0].title"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price & % move",
      "volume context",
      "headline"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "volume",
      "volAvg",
      "vol_vs_avg_pct",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      60
    ]
  }
}
