{
  "task_id": "nvda_summary_2025-10-05_v1",
  "user_prompt": "Summarize NVDA today in three bullets: price & % move, trading volume context (high/low vs typical), and one headline that might explain the move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "pct_move = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ],
        "next_args_from": "volume"
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_aggs",
      "params": {
        "ticker": "NVDA",
        "from": "2025-09-05",
        "to": "2025-10-05",
        "current_volume": "${volume}"
      },
      "analysis_requirements": {
        "extract": [
          "aggs = results[]"
        ],
        "compute": [
          "volumes = [item.volume for item in aggs]",
          "median_volume = median(volumes)",
          "avg_volume = sum(volumes) / len(volumes)",
          "volume_ratio = current_volume / median_volume",
          "volume_context = \"higher\" if current_volume > median_volume else \"lower\""
        ],
        "select": [],
        "accept_if": [
          "len(aggs) > 0",
          "median_volume > 0"
        ]
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "headlines = results[][title]"
        ],
        "compute": [
          "headline = head(headlines, 1)"
        ],
        "select": [],
        "accept_if": [
          "len(headlines) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price",
      "pct_move",
      "volume_context",
      "headline"
    ],
    "grounded_from": [
      "price",
      "pct_move",
      "volume_context",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
