{
  "task_id": "eq_market_open_nvda_001",
  "user_prompt": "Is the US equity market currently open? If closed, report the next open time; if open, report one high-impact NVDA headline from a credible source.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "Is the US stock market open now? site:marketwatch.com OR site:nyse.com OR site:nasdaq.com OR site:investing.com",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]"
        ],
        "compute": [
          "top = head(search_results, 1)",
          "top_url = top[0].url"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0"
        ],
        "next_args_from": "top_url"
      }
    },
    {
      "step": 2,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "url": "${top_url}"
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "published_at",
          "body",
          "source"
        ],
        "compute": [
          "open_mentions = regex_extract_all(body, \"(?i)\\\\b(open|closed|opens at|reopens at)\\\\b\")",
          "next_open_time_matches = regex_extract_all(body, \"(?i)(opens at|reopens at)\\\\s+([0-9]{1,2}:[0-9]{2}\\\\s?(AM|PM|am|pm)\\\\s?ET)\")",
          "next_open_time = head(next_open_time_matches, 1)",
          "is_open = contains(open_mentions, \"open\")"
        ],
        "select": [],
        "accept_if": [
          "body is not None",
          "title is not None",
          "len(open_mentions) > 0"
        ],
        "next_args_from": ""
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top = head(articles, 1)",
          "nvda_headline = top[0].title",
          "nvda_source = top[0].source"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "nvda_headline is not None"
        ],
        "next_args_from": ""
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "market_status",
      "nvda_headline",
      "next_open_time"
    ],
    "grounded_from": [
      "is_open",
      "nvda_headline",
      "nvda_source",
      "next_open_time",
      "title",
      "source"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "grounded in extracted sources (include source names or URLs when available)"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.35,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      80
    ]
  }
}
