{
  "task_id": "equities_aapl_snapshot_001",
  "user_prompt": "Give me a two-sentence market snapshot for AAPL today: current price, 1-day % change, and one concise risk from the latest credible headline.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon: polygon_get_aggs, polygon_get_news",
    "fmp: fmp_get_quote, fmp_get_income_statement, fmp_get_company_profile",
    "tavily: tavily_search, tavily_extract",
    "slack: send_slack_message, list_slack_channels",
    "python_execution: execute_python, process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume >= 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "latest = head(articles, 1)",
          "headline = latest[0].title",
          "risk_terms = regex_extract_all(headline, \"risk|concern|warning|slowdown|regulatory\")"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "must_include": [
      "current price",
      "1-day % change",
      "headline risk"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "headline"
    ],
    "quality_criteria": [
      "format: text",
      "two sentences",
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
