{
  "task_id": "nvda_daily_three_bullet_summary_v1",
  "user_prompt": "Summarize NVDA today in three bullets: price & % move, trading volume context (high/low vs typical), and one headline that might explain the move.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "quote_state = {\"price\": price, \"changesPercentage\": changesPercentage, \"volume\": volume}"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ],
        "next_args_from": "quote_state"
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_aggs",
      "params": {
        "ticker": "NVDA",
        "from": "2025-09-05",
        "to": "2025-10-05",
        "timespan": "day",
        "limit": 30,
        "quote_state": "${quote_state}"
      },
      "analysis_requirements": {
        "extract": [
          "aggs = results[]"
        ],
        "compute": [
          "avg_volume = sum([a.v for a in aggs]) / len(aggs)",
          "combined_state = {\"price\": quote_state[\"price\"], \"changesPercentage\": quote_state[\"changesPercentage\"], \"volume\": quote_state[\"volume\"], \"avg_volume\": avg_volume}"
        ],
        "select": [],
        "accept_if": [
          "len(aggs) > 5"
        ],
        "next_args_from": "combined_state"
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5,
        "combined_state": "${combined_state}"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "headline = head(articles, 1)[0].title",
          "final_state = {\"price\": combined_state[\"price\"], \"changesPercentage\": combined_state[\"changesPercentage\"], \"volume\": combined_state[\"volume\"], \"avg_volume\": combined_state[\"avg_volume\"], \"headline\": headline}"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price_move",
      "volume_context",
      "headline"
    ],
    "grounded_from": [
      "final_state"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
