{
  "task_id": "equities-msft-latest-headlines-price-move-001",
  "user_prompt": "Pull two latest credible headlines for MSFT and explain in one sentence how they might relate to today’s price move.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack_send_slack_message",
    "slack_list_slack_channels",
    "python_execution_execute_python",
    "python_execution_process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "MSFT",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "latest2 = head(articles, 2)",
          "latest2_titles = head(articles[][title], 2)",
          "latest2_urls = head(articles[][url], 2)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) >= 2"
        ],
        "next_args_from": ""
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "MSFT"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0"
        ],
        "next_args_from": ""
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "headline1",
      "headline2",
      "price",
      "one_sentence_explanation"
    ],
    "grounded_from": [
      "latest2_titles",
      "latest2_urls",
      "price",
      "changesPercentage"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "each headline must include source URL",
      "explanation grounded in extracted price and change"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      80
    ]
  }
}
