{
  "task_id": "task_apple_headline_001",
  "user_prompt": "Find Apple’s business description and give one sentence linking that to a current headline about the company.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon: polygon_get_aggs",
    "polygon: polygon_get_news",
    "fmp: fmp_get_quote",
    "fmp: fmp_get_income_statement",
    "fmp: fmp_get_company_profile",
    "tavily: tavily_search",
    "tavily: tavily_extract",
    "slack: send_slack_message",
    "slack: list_slack_channels",
    "python_execution: execute_python",
    "python_execution: process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_company_profile",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "description",
          "industry",
          "sector"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "description is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = head(articles, 1)",
          "top_headline_title = top_article[0].title",
          "top_headline_url = top_article[0].article_url"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "top_headline_title is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "business_description",
      "headline"
    ],
    "grounded_from": [
      "description",
      "top_headline_title",
      "top_headline_url"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "grounded in extracted data"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      15,
      40
    ]
  }
}
