{
  "task_id": "equities-meta-short-note-001",
  "user_prompt": "For META, produce a short note with current price, % move, and a single plausible driver from reputable coverage.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "META"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "META",
        "limit": 3
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]",
          "sources = results[][source]"
        ],
        "compute": [
          "top_title = head(titles, 1)",
          "top_source = head(sources, 1)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "len(titles) > 0"
        ],
        "next_args_from": ""
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "current_price",
      "pct_move",
      "driver"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "top_title"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.05
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
