{
  "task_id": "equities_aapl_snapshot_001",
  "user_prompt": "Give me a two-sentence market snapshot for AAPL today: current price, 1-day % change, and one concise risk from the latest credible headline.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_changes = {\"price\": price, \"changesPercentage\": changesPercentage}"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ],
        "next_args_from": "price_changes"
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = head(articles, 1)",
          "headline = top_article[0].title",
          "publisher = top_article[0].publisher",
          "published_utc = top_article[0].published_utc"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ],
        "next_args_from": "headline"
      }
    },
    {
      "step": 3,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "headline": "${headline}",
        "price_bundle": "${price_changes}",
        "instructions": "Return a concise risk sentence (<=16 words) derived directly from the headline and normalize the price fields for final output."
      },
      "analysis_requirements": {
        "extract": [
          "risk_sentence",
          "final_price",
          "final_pct"
        ],
        "compute": [
          "final_price = price_bundle.price",
          "final_pct = price_bundle.changesPercentage",
          "risk_sentence = concat(headline)  # concise transformation performed by the python executor"
        ],
        "select": [],
        "accept_if": [
          "final_price is not None",
          "final_pct is not None",
          "len(risk_sentence) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "final_price",
      "final_pct",
      "risk_sentence"
    ],
    "grounded_from": [
      "final_price",
      "final_pct",
      "risk_sentence"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      40
    ]
  }
}
