{
  "task_id": "nvda_daily_summary_001",
  "user_prompt": "Summarize NVDA today in three bullets: price & % move, trading volume context (high/low vs typical), and one headline that might explain the move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack.send_slack_message",
    "slack.list_slack_channels",
    "python_execution.execute_python",
    "python_execution.process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "move_pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0",
          "changesPercentage is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_aggs",
      "params": {
        "ticker": "NVDA",
        "from": "2025-09-05",
        "to": "2025-10-05",
        "limit": 30
      },
      "analysis_requirements": {
        "extract": [
          "aggs = results[]"
        ],
        "compute": [
          "avg_vol = sum([item.v for item in aggs]) / len(aggs)"
        ],
        "select": [],
        "accept_if": [
          "len(aggs) >= 5",
          "avg_vol > 0"
        ],
        "next_args_from": "aggs"
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "headline = articles[0].title"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price_move",
      "volume_context",
      "headline"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "volume",
      "avg_vol",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "each bullet grounded in extracted/computed variables"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      80
    ]
  }
}
