{
  "task_id": "equities_snapshot_aapl_simple_001",
  "user_prompt": "Give me a two-sentence market snapshot for AAPL today: current price, 1-day % change, and one concise risk from the latest credible headline.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack_send_slack_message",
    "slack_list_slack_channels",
    "python_execution_execute_python",
    "python_execution_process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 4
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = head(articles)",
          "title = top_article.title"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "title is not None"
        ],
        "next_args_from": "articles"
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL",
        "articles": "${articles}"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage"
        ],
        "compute": [
          "quote_summary = concat(str(price),\"|\",str(changesPercentage))",
          "top_article = head(articles)",
          "title = top_article.title",
          "context = concat(quote_summary,\"||\",title)"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "title is not None"
        ],
        "next_args_from": "context"
      }
    },
    {
      "step": 3,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "context": "${context}",
        "task": "parse_context_and_extract_risk"
      },
      "analysis_requirements": {
        "extract": [],
        "compute": [
          "parts = regex_extract_all(context, \"([^|]+)\\\\|([^|]+)\\\\|\\\\|(.+)\")",
          "price = parts[0] if len(parts) >= 3 else None",
          "changesPercentage = parts[1] if len(parts) >= 3 else None",
          "title = parts[2] if len(parts) >= 3 else None",
          "risk_matches = regex_extract_all(title, \"(decline|lawsuit|recall|slowdown|warn|investigation|layoff|regulatory|downgrade|concern|earnings)\")",
          "concise_risk = head(risk_matches) if len(risk_matches) > 0 else \"no acute headline risk found\""
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ],
        "next_args_from": ""
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "current_price",
      "1d_change_pct",
      "headline_risk"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "concise_risk"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "use only cited headline for the risk"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.4,
      "clarity": 0.1,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      40
    ]
  }
}
