{
  "task_id": "equities_us_market_open_nvda_001",
  "user_prompt": "Is the US equity market currently open? If closed, report the next open time; if open, report one high-impact NVDA headline from a credible source.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon:polygon_get_aggs",
    "polygon:polygon_get_news",
    "fmp:fmp_get_quote",
    "fmp:fmp_get_income_statement",
    "fmp:fmp_get_company_profile",
    "tavily:tavily_search",
    "tavily:tavily_extract",
    "slack:send_slack_message",
    "slack:list_slack_channels",
    "python_execution:execute_python",
    "python_execution:process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "code": "from datetime import datetime, time, timedelta\nfrom zoneinfo import ZoneInfo\n# Determine current time in US/Eastern and whether regular US equity market hours (Mon-Fri 09:30-16:00 ET) are active.\nnow = datetime.now(ZoneInfo('US/Eastern'))\nweekday = now.weekday()  # 0=Mon .. 6=Sun\nopen_time = time(9, 30)\nclose_time = time(16, 0)\n# Market open if weekday and time in range\nis_open = (weekday < 5) and (open_time <= now.time() < close_time)\nif is_open:\n    next_open = now.isoformat()\nelse:\n    # Find next weekday and set to 09:30 ET\n    next_day = now\n    while True:\n        next_day = next_day + timedelta(days=1)\n        if next_day.weekday() < 5:\n            next_open = next_day.replace(hour=9, minute=30, second=0, microsecond=0).isoformat()\n            break\n# Return structured result\nreturn {\"is_open\": is_open, \"next_open\": next_open, \"now\": now.isoformat()}"
      },
      "analysis_requirements": {
        "extract": [
          "is_open",
          "next_open",
          "now"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "is_open in [True, False]",
          "next_open is not None",
          "now is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "candidate = head(articles, 1)",
          "headline = candidate[0].title",
          "source = candidate[0].publisher.name",
          "published = candidate[0].published_utc"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "market_status",
      "next_open_time",
      "nvda_headline",
      "nvda_headline_source",
      "timestamp_checked"
    ],
    "grounded_from": [
      "is_open",
      "next_open",
      "headline",
      "source",
      "now"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "ground statements in extracted data"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.4,
      "clarity": 0.15,
      "safety": 0.05
    },
    "target_length_range": [
      30,
      80
    ]
  }
}
