{
  "task_id": "msft-news-price-001",
  "user_prompt": "Pull two latest credible headlines for MSFT and explain in one sentence how they might relate to today’s price move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon: polygon_get_aggs, polygon_get_news",
    "fmp: fmp_get_quote, fmp_get_income_statement, fmp_get_company_profile",
    "tavily: tavily_search, tavily_extract",
    "slack: send_slack_message, list_slack_channels",
    "python_execution: execute_python, process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 8
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "MSFT",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top2 = head(articles, 2)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 1"
        ]
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "MSFT"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_change_pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "must_include": [
      "headline_1",
      "headline_2",
      "price_change_pct"
    ],
    "grounded_from": [
      "top2",
      "price",
      "changesPercentage"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise (one sentence relating headlines to price move)",
      "format: text"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.25,
      "grounding": 0.45,
      "clarity": 0.25,
      "safety": 0.05
    },
    "target_length_range": [
      20,
      50
    ]
  }
}
