{
  "task_id": "equities_spy_1day_move_001",
  "user_prompt": "Report the 1-day move for SPY and cite a single headline that plausibly explains it. Keep it under 80 words.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon: polygon_get_aggs, polygon_get_news",
    "fmp: fmp_get_quote, fmp_get_income_statement, fmp_get_company_profile",
    "tavily: tavily_search, tavily_extract",
    "slack: send_slack_message, list_slack_channels",
    "python_execution: execute_python, process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "SPY"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "SPY",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "headline = head(articles, 1)[0].title"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "must_include": [
      "1-day move",
      "headline"
    ],
    "grounded_from": [
      "changesPercentage",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise (<= 80 words)",
      "cite the single headline provided"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.05
    },
    "target_length_range": [
      10,
      80
    ]
  }
}
