{
  "task_id": "equities_aapl_description_headline_001",
  "user_prompt": "Find Apple’s business description and give one sentence linking that to a current headline about the company.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon: polygon_get_aggs, polygon_get_news",
    "fmp: fmp_get_quote, fmp_get_income_statement, fmp_get_company_profile",
    "tavily: tavily_search, tavily_extract",
    "slack: send_slack_message, list_slack_channels",
    "python_execution: execute_python, process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_company_profile",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "description",
          "industry",
          "sector"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "description is not None"
        ],
        "next_args_from": "description"
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5,
        "description": "${description}"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "top_title = head(titles, 1)",
          "headline = top_title[0]",
          "one_sentence_link = concat(description, \" — \", headline)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "len(titles) > 0",
          "description is not None",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "business_description",
      "headline",
      "one_sentence_link"
    ],
    "grounded_from": [
      "description",
      "headline",
      "one_sentence_link"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
