{
  "task_id": "equities_nflx_slack_one_liner_v1",
  "user_prompt": "Send me (in Slack) a one-liner on NFLX with price, % move, and a link to one credible article explaining the move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon: polygon_get_aggs",
    "polygon: polygon_get_news",
    "fmp: fmp_get_quote",
    "fmp: fmp_get_income_statement",
    "fmp: fmp_get_company_profile",
    "tavily: tavily_search",
    "tavily: tavily_extract",
    "slack: send_slack_message",
    "slack: list_slack_channels",
    "python_execution: execute_python",
    "python_execution: process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NFLX"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NFLX",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = head(articles, 1)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ],
        "next_args_from": "top_article"
      }
    },
    {
      "step": 3,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "price": "${price}",
        "changesPercentage": "${changesPercentage}",
        "top_article": "${top_article}"
      },
      "analysis_requirements": {
        "extract": [],
        "compute": [
          "price_str = str(price)",
          "pct_str = str(changesPercentage)",
          "article_url = top_article.url if isinstance(top_article, dict) and 'url' in top_article else None",
          "message = concat('NFLX price: $', price_str, ' (', pct_str, '%) — ', article_url)"
        ],
        "select": [],
        "accept_if": [
          "article_url is not None",
          "len(message) > 10"
        ],
        "next_args_from": "message"
      }
    },
    {
      "step": 4,
      "server": "slack",
      "tool": "send_slack_message",
      "params": {
        "channel": "#research",
        "message": "${message}"
      },
      "analysis_requirements": {
        "extract": [
          "ok"
        ],
        "compute": [
          "price = ${price}",
          "percent_change = ${changesPercentage}",
          "article_url = ${top_article}.url"
        ],
        "select": [],
        "accept_if": [
          "ok == true"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price",
      "percent_change",
      "article_url"
    ],
    "grounded_from": [
      "price",
      "percent_change",
      "article_url"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      8,
      20
    ]
  }
}
