{
  "task_id": "equities_top_mover_today_001",
  "user_prompt": "Between AAPL, MSFT, and NVDA, which had the largest % move today? Provide the ranking, one plausible driver headline for the top mover, and a 2-sentence explanation.",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume",
          "symbol"
        ],
        "compute": [
          "pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ],
        "next_args_from": "quote_AAPL"
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "MSFT"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume",
          "symbol"
        ],
        "compute": [
          "pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ],
        "next_args_from": "quote_MSFT"
      }
    },
    {
      "step": 3,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume",
          "symbol"
        ],
        "compute": [
          "pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ],
        "next_args_from": "quote_NVDA"
      }
    },
    {
      "step": 4,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "function": "compute_ranking",
        "args": {
          "quotes": "${quote_AAPL},${quote_MSFT},${quote_NVDA}"
        },
        "notes": "Compute ranking of the three symbols by absolute daily percent change (use changesPercentage fields). Return a list of dicts sorted desc by abs(pct): [{'symbol':..., 'pct':...}, ...], plus top_symbol and top_pct."
      },
      "analysis_requirements": {
        "extract": [
          "ranking",
          "top_symbol",
          "top_pct"
        ],
        "compute": [
          "top_three = head(ranking, 3)"
        ],
        "select": [],
        "accept_if": [
          "len(ranking) == 3",
          "top_symbol in ['AAPL','MSFT','NVDA']",
          "top_pct is not None"
        ],
        "next_args_from": "top_symbol"
      }
    },
    {
      "step": 5,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "${top_symbol}",
        "limit": 5,
        "timespan_hint": "today"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = head(articles, 1)",
          "headline = top_article[0].title if len(top_article) > 0 else None"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "must_include": [
      "ranking",
      "top_mover_headline",
      "2-sentence_explanation"
    ],
    "grounded_from": [
      "ranking",
      "top_symbol",
      "top_pct",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "format: text"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      40,
      120
    ]
  }
}
