{
  "task_id": "equities_factcheck_apple_001",
  "user_prompt": "Fact-check the claim “Apple announced a new product this week.” Verify with at least two independent sources and state your verdict with citations.",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon.polygon_get_news",
    "polygon.polygon_get_aggs",
    "fmp.fmp_get_quote",
    "fmp.fmp_get_income_statement",
    "fmp.fmp_get_company_profile",
    "tavily.tavily_search",
    "tavily.tavily_extract",
    "slack.send_slack_message",
    "slack.list_slack_channels",
    "python_execution.execute_python",
    "python_execution.process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 20
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top1 = head(articles, 1)",
          "top_title = top1[0].title",
          "matched_articles = [a for a in articles if (\"announce\" in ((a.get('title') or '').lower()) or \"announce\" in ((a.get('description') or '').lower()))]",
          "num_matches = len(matched_articles)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "top_title is not None"
        ],
        "next_args_from": "top_title"
      }
    },
    {
      "step": 2,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "${top_title} Apple announcement",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]"
        ],
        "compute": [
          "top_search = head(search_results, 1)",
          "result_url = top_search[0].url"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0",
          "result_url is not None"
        ],
        "next_args_from": "result_url"
      }
    },
    {
      "step": 3,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "url": "${result_url}"
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "published_at",
          "content",
          "authors = authors[]"
        ],
        "compute": [
          "tavily_article = {\"title\": title, \"published_at\": published_at, \"snippet\": content[:300], \"authors\": authors}"
        ],
        "select": [],
        "accept_if": [
          "content is not None",
          "len(content) > 100"
        ],
        "next_args_from": "tavily_article"
      }
    },
    {
      "step": 4,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "timestamp"
        ],
        "compute": [
          "quote = {\"price\": price, \"changes\": changesPercentage, \"timestamp\": timestamp}",
          "final_bundle = {\"matched_articles\": matched_articles, \"tavily_article\": tavily_article, \"quote\": quote}"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "markdown",
    "must_include": [
      "verdict",
      "source_count",
      "citations"
    ],
    "grounded_from": [
      "matched_articles",
      "tavily_article",
      "quote"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "grounded"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      80,
      200
    ]
  }
}
