{
  "task_id": "equities_competitor_context_amd_nvda_001",
  "user_prompt": "Create a concise competitor context for AMD by contrasting one AMD headline with one NVDA headline, then state how this might influence investor sentiment today.",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack_send_slack_message",
    "slack_list_slack_channels",
    "python_execution_execute_python",
    "python_execution_process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AMD",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "amd_top = head(articles, 1)",
          "amd_headline = amd_top[0].title",
          "amd_url = amd_top[0].url"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "amd_headline is not None"
        ],
        "next_args_from": "amd_headline"
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "nvda_top = head(articles, 1)",
          "nvda_headline = nvda_top[0].title",
          "nvda_url = nvda_top[0].url"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "nvda_headline is not None"
        ],
        "next_args_from": "nvda_headline"
      }
    },
    {
      "step": 3,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AMD"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "amd_price = price",
          "amd_changesPercentage = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ],
        "next_args_from": "amd_changesPercentage"
      }
    },
    {
      "step": 4,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "nvda_price = price",
          "nvda_changesPercentage = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ],
        "next_args_from": "nvda_changesPercentage"
      }
    },
    {
      "step": 5,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "script": "compose_competitor_context",
        "inputs": {
          "amd_headline": "${amd_headline}",
          "nvda_headline": "${nvda_headline}",
          "amd_changesPercentage": "${amd_changesPercentage}",
          "nvda_changesPercentage": "${nvda_changesPercentage}"
        }
      },
      "analysis_requirements": {
        "extract": [],
        "compute": [
          "AMD_headline = amd_headline",
          "NVDA_headline = nvda_headline",
          "amd_changes = float(amd_changesPercentage)",
          "nvda_changes = float(nvda_changesPercentage)",
          "if nvda_changes > amd_changes:\n    Investor_sentiment_inference = concat('Investors likely favor NVDA today due to stronger intraday price momentum (', str(nvda_changes), '% vs ', str(amd_changes), '%).')\nelse:\n    Investor_sentiment_inference = concat('Investor reaction is more favorable or mixed toward AMD today (', str(amd_changes), '% vs ', str(nvda_changes), '%).')"
        ],
        "select": [],
        "accept_if": [
          "len(AMD_headline) > 0",
          "len(NVDA_headline) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "AMD_headline",
      "NVDA_headline",
      "Investor_sentiment_inference"
    ],
    "grounded_from": [
      "amd_headline",
      "nvda_headline",
      "amd_changesPercentage",
      "nvda_changesPercentage"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "ground claims in extracted headlines and price changes"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      40,
      100
    ]
  }
}
