  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "MSFT",
        "limit": 20,
        "published_on": "2025-10-06"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "matched_titles_MSFT = regex_extract_all(articles[][title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")",
          "num_matches_MSFT = len(matched_titles_MSFT)",
          "reg_articles_MSFT = head([a for a in articles if regex_extract_all([a.title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")], 3)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "GOOGL",
        "limit": 20,
        "published_on": "2025-10-06"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "matched_titles_GOOGL = regex_extract_all(articles[][title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")",
          "num_matches_GOOGL = len(matched_titles_GOOGL)",
          "reg_articles_GOOGL = head([a for a in articles if regex_extract_all([a.title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")], 3)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "META",
        "limit": 20,
        "published_on": "2025-10-06"
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "matched_titles_META = regex_extract_all(articles[][title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")",
          "num_matches_META = len(matched_titles_META)",
          "reg_articles_META = head([a for a in articles if regex_extract_all([a.title], \"(?i)antitrust|probe|investigation|regulator|regulatory|FTC|fine|EU|lawsuit|suit|charged|charging\")], 3)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    },
    {
      "step": 4,
      "server": "python_execution",
      "tool": "execute_python",
      "params": {
        "script": "choose_max_and_collect_headlines"
      },
      "analysis_requirements": {
        "extract": [],
        "compute": [
          "scores = {\"MSFT\": num_matches_MSFT, \"GOOGL\": num_matches_GOOGL, \"META\": num_matches_META}",
          "chosen_ticker = max(scores, key=scores.get)",
          "reg_headlines = (reg_articles_MSFT if chosen_ticker == 'MSFT' else (reg_articles_GOOGL if chosen_ticker == 'GOOGL' else reg_articles_META))",
          "top_headline = head(reg_headlines, 1)[0] if len(reg_headlines) > 0 else None"
        ],
        "select": [],
        "accept_if": [
          "max(num_matches_MSFT, num_matches_GOOGL, num_matches_META) > 0"
        ],
        "next_args_from": "chosen_ticker"
      }
    },
    {
      "step": 5,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "${chosen_ticker}"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "chosen_ticker",
      "price",
      "changesPercentage",
      "reg_headline"
    ],
    "grounded_from": [
      "chosen_ticker",
      "reg_headlines",
      "price",
      "changesPercentage"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "≤2 sentences explaining the risk",
      "ground statements in the extracted headlines and price data"
    ]
  },
  "judge_rubric": {
