{
  "task_id": "nvda_bearish_vs_positive_001",
  "user_prompt": "Find one credible bearish article on NVDA today and contrast it with one neutral/positive piece. Summarize implications for near-term price action (≤120 words).",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "slack_send_slack_message",
    "slack_list_slack_channels",
    "python_execution_execute_python",
    "python_execution_process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "NVIDIA NVDA bearish today",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]"
        ],
        "compute": [
          "bearish_top = head(search_results, 1)"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0"
        ],
        "next_args_from": "bearish_top"
      }
    },
    {
      "step": 2,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "item": "${bearish_top}",
        "extract_full": true
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "content",
          "published_at",
          "source"
        ],
        "compute": [
          "neg_terms = regex_extract_all(content, \"(?i)loss|miss|cut|downgrade|sell|bearish|slowing\")",
          "bearish_article_title = title",
          "bearish_article_content = content"
        ],
        "select": [],
        "accept_if": [
          "content is not None",
          "len(neg_terms) > 0"
        ]
      }
    },
    {
      "step": 3,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "NVIDIA NVDA bullish OR positive today",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]"
        ],
        "compute": [
          "positive_top = head(search_results, 1)"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0"
        ],
        "next_args_from": "positive_top"
      }
    },
    {
      "step": 4,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "item": "${positive_top}",
        "extract_full": true
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "content",
          "published_at",
          "source"
        ],
        "compute": [
          "pos_terms = regex_extract_all(content, \"(?i)beat|raise|upgrade|outperform|positive|bullish|strong\")",
          "positive_article_title = title",
          "positive_article_content = content"
        ],
        "select": [],
        "accept_if": [
          "content is not None",
          "len(title) > 0"
        ]
      }
    },
    {
      "step": 5,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_move = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0",
          "changesPercentage is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "bearish_article_title",
      "positive_article_title",
      "implication_summary"
    ],
    "grounded_from": [
      "bearish_article_title",
      "positive_article_title",
      "price",
      "changesPercentage"
    ],
    "quality_criteria": [
      "no hallucinations",
      "≤120 words",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      120
    ]
  }
}
