{
  "task_id": "equities_aapl_snapshot_001",
  "user_prompt": "Give me a two-sentence market snapshot for AAPL today: current price, 1-day % change, and one concise risk from the latest credible headline.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top_article = first(articles)",
          "top_url = safe_attr(top_article, \"url\")",
          "top_title = safe_attr(top_article, \"title\")"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "top_url is not None"
        ],
        "next_args_from": "top_url"
      }
    },
    {
      "step": 3,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "url": "${top_url}"
      },
      "analysis_requirements": {
        "extract": [
          "content",
          "title"
        ],
        "compute": [
          "sentences = regex_extract_all(\"[^.?!]+[.?!]\", content)",
          "risk_sentence = first(sentences)"
        ],
        "select": [],
        "accept_if": [
          "len(sentences) > 0",
          "risk_sentence is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "current_price",
      "one_day_pct_change",
      "headline_risk"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "risk_sentence"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
