{
  "task_id": "nvda_daily_three_bullet_summary_v1",
  "user_prompt": "Summarize NVDA today in three bullets: price & % move, trading volume context (high/low vs typical), and one headline that might explain the move.",
  "complexity": "simple",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_aggs",
      "params": {
        "ticker": "NVDA",
        "from": "2025-09-06",
        "to": "2025-10-06",
        "timespan": "day",
        "limit": 30
      },
      "analysis_requirements": {
        "extract": [
          "aggs = results[]",
          "vols = results[][v]"
        ],
        "compute": [
          "median_vol = median(vols)",
          "latest_vol = first(vols)",
          "vol_ratio = latest_vol / median_vol"
        ],
        "select": [],
        "accept_if": [
          "len(aggs) > 0",
          "median_vol > 0"
        ]
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "headline = safe_attr(first(articles), 'title')"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "must_include": [
      "price_move",
      "volume_context",
      "headline"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "latest_vol",
      "median_vol",
      "headline"
    ],
    "quality_criteria": [
      "format: text; three bullets",
      "no hallucinations; ground facts to extracted variables",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      30,
      80
    ]
  }
}
