{
  "task_id": "msft_headlines_price_move_001",
  "user_prompt": "Pull two latest credible headlines for MSFT and explain in one sentence how they might relate to today’s price move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 8
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "MSFT",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "headlines = head(articles[][title], 2)",
          "sources = head(articles[][publisher], 2)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) >= 2"
        ]
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "MSFT"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_change = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "headlines",
      "price_change"
    ],
    "grounded_from": [
      "headlines",
      "price_change"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "ground each claim in the extracted variables"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      20,
      60
    ]
  }
}
