{
  "task_id": "equities_spy_1d_headline_001",
  "user_prompt": "Report the 1-day move for SPY and cite a single headline that plausibly explains it. Keep it under 80 words.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "SPY"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "SPY",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "headline = first(titles)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "1-day move",
      "headline"
    ],
    "grounded_from": [
      "pct",
      "headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "under 80 words"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      8,
      80
    ]
  }
}
