{
  "task_id": "srch_equities_apple_001",
  "user_prompt": "Find Apple’s business description and give one sentence linking that to a current headline about the company.",
  "complexity": "simple",
  "max_turns": 3,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_company_profile",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "description",
          "industry",
          "sector"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "description is not None"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]",
          "urls = results[][url]",
          "published_at = results[][published_utc]"
        ],
        "compute": [
          "top_headline = first(titles)",
          "top_url = first(urls)",
          "top_time = first(published_at)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "business_description",
      "headline"
    ],
    "grounded_from": [
      "description",
      "top_headline"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.4,
      "clarity": 0.15,
      "safety": 0.05
    },
    "target_length_range": [
      15,
      40
    ]
  }
}
