{
  "task_id": "equities_nflx_slack_one_liner_001",
  "user_prompt": "Send me (in Slack) a one-liner on NFLX with price, % move, and a link to one credible article explaining the move.",
  "complexity": "simple",
  "max_turns": 4,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 3
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NFLX"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NFLX",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]",
          "urls = results[][url]"
        ],
        "compute": [
          "article_url = first(urls)",
          "article_title = first(titles)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ],
        "next_args_from": "article_url"
      }
    },
    {
      "step": 3,
      "server": "slack",
      "tool": "send_slack_message",
      "params": {
        "channel": "#equity-research",
        "message": "NFLX ${price} (${changesPercentage}%) — article: ${article_url}"
      },
      "analysis_requirements": {
        "extract": [
          "ok"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "ok == True"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price",
      "changesPercentage",
      "article_url"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "article_url"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.05
    },
    "target_length_range": [
      8,
      20
    ]
  }
}
