{
  "task_id": "equities-regulatory-risk-001",
  "user_prompt": "Identify one stock among MSFT, GOOGL, META whose headlines suggest a regulatory risk today. Provide price context and ≤2 sentences explaining the risk.",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "MSFT regulatory investigation OR antitrust OR SEC OR fine today",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "reg_matches = regex_extract_all(\"(?i)\\\\b(antitrust|investigation|SEC|regulator|fine|probe)\\\\b\", concat(titles))",
          "ticker_candidate = \"MSFT\""
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0",
          "len(reg_matches) > 0"
        ],
        "next_args_from": "ticker_candidate"
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "${ticker_candidate}"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "volume > 0"
        ]
      }
