{
  "task_id": "equities_watchlist_digest_001",
  "user_prompt": "Produce a compact watchlist digest for AAPL, MSFT, NVDA: price, % move, and one plausible driver headline each. End with a 2-sentence overview.",
  "complexity": "moderate",
  "max_turns": 8,
  "tools_available": [
    "polygon: polygon_get_aggs",
    "polygon: polygon_get_news",
    "fmp: fmp_get_quote",
    "fmp: fmp_get_income_statement",
    "fmp: fmp_get_company_profile",
    "tavily: tavily_search",
    "tavily: tavily_extract",
    "slack: send_slack_message",
    "slack: list_slack_channels",
    "python_execution: execute_python",
    "python_execution: process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "AAPL"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_AAPL = price",
          "pct_AAPL = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "AAPL",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "headline_AAPL = first(titles)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline_AAPL is not None"
        ]
      }
    },
    {
      "step": 3,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "MSFT"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_MSFT = price",
          "pct_MSFT = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 4,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "MSFT latest news Microsoft earnings regulatory product headlines",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "headline_MSFT = first(titles)"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0",
          "headline_MSFT is not None"
        ]
      }
    },
    {
      "step": 5,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_NVDA = price",
          "pct_NVDA = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 6,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]"
        ],
        "compute": [
          "headline_NVDA = first(titles)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline_NVDA is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price_AAPL",
      "pct_AAPL",
      "headline_AAPL",
      "price_MSFT",
      "pct_MSFT",
      "headline_MSFT",
      "price_NVDA",
      "pct_NVDA",
      "headline_NVDA",
      "two_sentence_overview"
    ],
    "grounded_from": [
      "price_AAPL",
      "pct_AAPL",
      "headline_AAPL",
      "price_MSFT",
      "pct_MSFT",
      "headline_MSFT",
      "price_NVDA",
      "pct_NVDA",
      "headline_NVDA"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "each ticker line includes price, percent move, and a sourced plausible headline"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      50,
      120
    ]
  }
}
