{
  "task_id": "nvda_investor_brief_001",
  "user_prompt": "Create a one-page investor brief for NVDA combining: price/% move, concise company context, three diverse headlines (with sources), and a balanced risk-vs-opportunity summary.",
  "complexity": "complex",
  "max_turns": 8,
  "tools_available": [
    "polygon: polygon_get_aggs, polygon_get_news",
    "fmp: fmp_get_quote, fmp_get_income_statement, fmp_get_company_profile",
    "tavily: tavily_search, tavily_extract",
    "slack: send_slack_message, list_slack_channels",
    "python_execution: execute_python, process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "fmp",
      "tool": "fmp_get_company_profile",
      "params": {
        "symbol": "NVDA"
      },
      "analysis_requirements": {
        "extract": [
          "description",
          "industry",
          "sector"
        ],
        "compute": [],
        "select": [],
        "accept_if": [
          "description is not None",
          "industry is not None"
        ]
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "NVDA",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]"
        ],
        "compute": [
          "top2 = head(articles, 2)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0"
        ],
        "next_args_from": ""
      }
    },
    {
      "step": 4,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "NVIDIA Q3 2025 earnings OR Nvidia AI chips supply chain",
        "limit": 5
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]"
        ],
        "compute": [
          "top1 = head(search_results, 1)",
          "top1_title = safe_attr(first(top1), \"title\")",
          "top1_url = safe_attr(first(top1), \"url\")"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0"
        ],
        "next_args_from": "top1_url"
      }
    },
    {
      "step": 5,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "url": "${top1_url}"
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "source",
          "content"
        ],
        "compute": [
          "tavily_title = title",
          "tavily_source = source"
        ],
        "select": [],
        "accept_if": [
          "title is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price",
      "percent_move",
      "company_context",
      "headline_1",
      "headline_2",
      "headline_3",
      "risks_and_opportunities"
    ],
    "grounded_from": [
      "price",
      "changesPercentage",
      "description",
      "industry",
      "top2",
      "tavily_title",
      "tavily_source"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "grounded in cited sources"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.35,
      "grounding": 0.35,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      180,
      320
    ]
  }
}
