{
  "task_id": "meta_move_today_brief_v1",
  "user_prompt": "Draft a brief for a PM: Why did META move today? Include price/% move, two corroborating headlines from different outlets, and a risk/mitigation bullet.",
  "complexity": "complex",
  "max_turns": 8,
  "tools_available": [
    "polygon_get_aggs",
    "polygon_get_news",
    "fmp_get_quote",
    "fmp_get_income_statement",
    "fmp_get_company_profile",
    "tavily_search",
    "tavily_extract",
    "send_slack_message",
    "list_slack_channels",
    "execute_python",
    "process_mcp_data"
  ],
  "limits": {
    "max_tools": 5,
    "max_servers": 5
  },
  "tool_sequence": [
    {
      "step": 1,
      "server": "fmp",
      "tool": "fmp_get_quote",
      "params": {
        "symbol": "META"
      },
      "analysis_requirements": {
        "extract": [
          "price",
          "changesPercentage",
          "volume"
        ],
        "compute": [
          "price_move_pct = changesPercentage"
        ],
        "select": [],
        "accept_if": [
          "price is not None",
          "changesPercentage is not None",
          "volume > 0"
        ]
      }
    },
    {
      "step": 2,
      "server": "polygon",
      "tool": "polygon_get_aggs",
      "params": {
        "ticker": "META",
        "from": "2025-10-05",
        "to": "2025-10-06",
        "limit": 500
      },
      "analysis_requirements": {
        "extract": [
          "aggs = results[]"
        ],
        "compute": [
          "intraday_pct = pct_change_last_day(aggs)"
        ],
        "select": [],
        "accept_if": [
          "len(aggs) > 0"
        ]
      }
    },
    {
      "step": 3,
      "server": "polygon",
      "tool": "polygon_get_news",
      "params": {
        "ticker": "META",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "articles = results[]",
          "titles = results[][title]",
          "publishers = results[][publisher]"
        ],
        "compute": [
          "headline1 = first(titles)",
          "source1 = first(publishers)"
        ],
        "select": [],
        "accept_if": [
          "len(articles) > 0",
          "headline1 is not None"
        ]
      }
    },
    {
      "step": 4,
      "server": "tavily",
      "tool": "tavily_search",
      "params": {
        "query": "Meta Platforms stock move today",
        "limit": 10
      },
      "analysis_requirements": {
        "extract": [
          "search_results = results[]",
          "urls = search_results[][url]"
        ],
        "compute": [
          "top_url = first(urls)"
        ],
        "select": [],
        "accept_if": [
          "len(search_results) > 0",
          "top_url is not None"
        ],
        "next_args_from": "top_url"
      }
    },
    {
      "step": 5,
      "server": "tavily",
      "tool": "tavily_extract",
      "params": {
        "url": "${top_url}"
      },
      "analysis_requirements": {
        "extract": [
          "title",
          "site",
          "content"
        ],
        "compute": [
          "headline2 = title",
          "site2 = site"
        ],
        "select": [],
        "accept_if": [
          "headline2 is not None",
          "site2 is not None"
        ]
      }
    }
  ],
  "final_answer_requirements": {
    "format": "text",
    "must_include": [
      "price",
      "price_move_pct",
      "headline1",
      "headline2",
      "risk_bullet"
    ],
    "grounded_from": [
      "price",
      "price_move_pct",
      "headline1",
      "headline2",
      "source1",
      "site2",
      "intraday_pct"
    ],
    "quality_criteria": [
      "no hallucinations",
      "concise",
      "grounded in cited headlines"
    ]
  },
  "judge_rubric": {
    "weights": {
      "coverage": 0.4,
      "grounding": 0.3,
      "clarity": 0.2,
      "safety": 0.1
    },
    "target_length_range": [
      80,
      200
    ]
  }
}
